update:
begin atomic update Doc D set D.SubConto_id=T.SubConto_id, D.day=now(), D.user_id=T.user_id, D.KindDoc_id=T.KindDoc_id, D.num=T.num, D.base_id=T.base_id, D.Remark=T.Remark, D.opl=0, D.list_id=23, D.place_id=(select place_id from SubConto S where S.id=T.SubConto_id) from Doc D join #d T on D.id=T.id;
insert into Doc(id,SubConto_id,day,user_id,KindDoc_id,num,base_id,opl,Remark,place_id) select id,SubConto_id,now(),user_id,KindDoc_id,num,base_id,0,Remark, (select place_id from SubConto S where S.id=T.SubConto_id) from #d T where not exists(select * from Doc D where D.id=T.id);
delete from DocStates where doc_id in (select id from #d) and n=0;
insert into DocStates(doc_id,n,tm,state_id,user_id) select id,0,now(),16,-1 from #d where not exists(select * from DocStates DS where DS.doc_id=#d.id and DS.n=0);
insert into DocStates(doc_id,n,tm,state_id,user_id) select doc_id,1+n,tm,state_id,user_id from #ds where not exists(select * from DocStates DS where DS.doc_id=#ds.doc_id and DS.n=#ds.n+1);
update DocRow R set R.item_id=T.item_id, R.price=T.price, R.price1=T.price1, R.vprice=T.vprice, R.vprice1=T.vprice1, R.quant=T.quant from DocRow R join #r T on R.doc_id=T.doc_id and R.id=T.id;
insert into DocRow(doc_id,id,item_id, price, price1,vprice,vprice1,quant) select doc_id,id,item_id, price, price1,vprice,vprice1,quant from #r where not exists(select * from DocRow R where R.doc_id=#r.doc_id and R.id=#r.id); end
